'use strict';

module.exports = function(grunt) {

    grunt.loadNpmTasks('grunt-contrib-jshint');
    grunt.loadNpmTasks('grunt-mocha-test');

    grunt.initConfig({
        jshint: {
            all: ['gruntfile.js', 'index.js', 'lib/**/*.js', 'test/**/*.js'],
            options: {
                expr: true,
                node: true,
                strict: true,
                globals: {
                    describe: true,
                    it: true,
                    before: true,
                    after: true,
                    beforeEach: true,
                    afterEach: true
                }
            }
        },
        mochaTest: {
            test: {
                options: {
                    reporter: 'spec'
                },
                src: ['test/**/*.js']
            }
        }
    });

    grunt.registerTask('default', ['jshint', 'mochaTest']);
};