'use strict';

var ssoPartnerRequest = require('./request');

function partner(endpoint) {
    var getEndpoint = endpoint;
    if (endpoint !== undefined && typeof endpoint !== 'function') {
        getEndpoint = function() {
            return endpoint;
        };
    }

    function getCourse(courseKey, callback) {
        ssoPartnerRequest(getEndpoint() + '/partner/rest/course', {
            courseKey: courseKey
        }, callback);
    }

    return {
        getCourse: getCourse
    };
}

module.exports = partner;
